mapper.select:
SELECT 
	* 
FROM 
	Opordheader 
WHERE 
	xtypetrn=#{xtypetrn}
	AND xpornum=#{xpornum}
	AND xcus=#{xcus}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid} 
	AND zactive='1'

SELECT 
	* 
FROM 
	Opordheader 
WHERE 
	xtypetrn=#{xtypetrn}
	AND xtrn=#{xtrn}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid} 
	AND zactive='1'

SELECT 
	* 
FROM 
	Opordheader 
WHERE 
	xtypetrn=#{xtypetrn}
	AND xtrn=#{xtrn}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid} 
	AND zactive='1'

SELECT 
	* 
FROM 
	oporddetail 
WHERE 
	xordernum=#{xordernum}
	AND xitem=#{xitem}
	AND zid=#{zid} 
	AND zactive='1'
