mapper.delete:
DELETE 
FROM 
	imtorHeader 
WHERE 
	xtornum=#{xtornum} 
	zid=#{zid}

DELETE 
FROM 
	ImtorDetail 
WHERE 
	xrow=#{xrow} 
	AND xtornum=#{xtornum} 
	AND zid=#{zid}

DELETE 
FROM 
	imtordetail 
WHERE 
	xtypetrn=#{xtypetrn} 
	AND zid=#{zid}

mapper.select:
SELECT 
	* 
FROM 
	imtorheader 
WHERE 
	xchalanref=#{xchalanref} 
	AND zid=#{zid}
	AND xfwh=#{xfwh}
	AND xtwh=#{xtwh}

SELECT 
	* 
FROM 
	ImtorDetail 
WHERE 
	xtornum=#{xtornum} 
	AND xchalanref=#{xchalanref} 
	AND zid=#{zid}

SELECT 
	* 
FROM 
	ImtorDetail 
WHERE 
	xtornum=#{xtornum} 
	AND xrow=#{xrow} 
	AND zid=#{zid}

SELECT 
	* 
FROM 
	ImtorDetail 
WHERE 
	xtornum=#{xtornum} 
	AND xitem=#{xitem} 
	AND zid=#{zid}
