mapper.insert:
SELECT COALESCE(Max(xrow), 0) + 1 FROM calanddag WHERE zactive='1' AND zid=#{zid}
INSERT INTO calanddag ( xrow, xland, xdagtype, xdagnum, xtype, xqty, xunit, zactive, zauserid, zid, ztime, zutime, zuuserid ) VALUES (
#{xrow}, #{xland, jdbcType=VARCHAR}, #{xdagtype, jdbcType=VARCHAR}, #{xdagnum, jdbcType=INTEGER}, #{xtype, jdbcType=VARCHAR}, #{xqty, jdbcType=DECIMAL}, #{xunit, jdbcType=VARCHAR}, #{zactive, jdbcType=VARCHAR}, #{zauserid, jdbcType=VARCHAR}, #{zid, jdbcType=VARCHAR}, #{ztime, jdbcType=TIMESTAMP}, #{zutime, jdbcType=TIMESTAMP}, #{zuuserid, jdbcType=VARCHAR} )
