mapper.delete:
DELETE
FROM
	OPORDDETAIL
WHERE
	xordernum=#{xordernum, jdbcType=VARCHAR}
	AND xrow=#{xrow, jdbcType=INTEGER}
	AND zid=#{zid, jdbcType=VARCHAR}

mapper.select:
SELECT 
	* 
FROM 
	Opordheader 
WHERE 
	xtypetrn=#{xtypetrn}
	AND xpornum=#{xpornum}
	AND xcus=#{xcus}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid} 
	AND zactive='1'
ORDER BY
	XORDERNUM DESC

SELECT 
	* 
FROM 
	Opordheader 
WHERE 
	xtypetrn=#{xtypetrn}
	AND xtrn=#{xtrn}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid} 
	AND zactive='1'
ORDER BY
	XORDERNUM DESC

SELECT 
	OPORDHEADER.*,
	ZBUSINESS.ZORG AS "branchname"
FROM 
	OPORDHEADER
	JOIN ZBUSINESS ON ZBUSINESS.ZID = OPORDHEADER.XCUS
WHERE
	OPORDHEADER.ZID=#{zid}
	AND OPORDHEADER.ZACTIVE='1'
	AND OPORDHEADER.XTYPETRN=#{xtypetrn}
	AND OPORDHEADER.XTRN=#{xtrn}
	AND OPORDHEADER.XSTATUS=#{xstatus}
	AND TO_CHAR(OPORDHEADER.XDATE,'yyyy-MM-dd')=#{xdate}
ORDER BY
	OPORDHEADER.XORDERNUM DESC

SELECT 
	* 
FROM 
	OPORDHEADER 
WHERE 
	(UPPER(XORDERNUM) LIKE '%' || #{xordernum} || '%') 
	AND ZID=#{zid} 
	AND XTYPETRN=#{xtypetrn}
	AND XTRN=#{xtrn}
	AND XSTATUS=#{xstatus}
	AND ZACTIVE='1'

select distinct opd.xitem from oporddetail opd join opordheader oh on oh.xordernum = opd.xordernum and oh.zid = opd.zid and oh.zactive='1' where opd.zid=#{zid} and opd.zactive='1' and opd.xcatitem=#{xcatitem}
and oh.xordernum != #{xordernum}
and (((to_char(oh.xstartdate, 'dd-MON-yyyy') || ' ' || oh.xstarttime) <= #{xstartdate} and (to_char(oh.xenddate, 'dd-MON-yyyy') || ' ' || oh.xendtime) >= #{xstartdate}) OR ((to_char(oh.xstartdate, 'dd-MON-yyyy') || ' ' || oh.xstarttime) <= #{xenddate} and (to_char(oh.xenddate, 'dd-MON-yyyy') || ' ' || oh.xendtime) >= #{xenddate}))
