mapper.delete:
DELETE
FROM
	OPORDDETAIL
WHERE
	xordernum=#{xordernum, jdbcType=VARCHAR}
	AND xrow=#{xrow, jdbcType=INTEGER}
	AND zid=#{zid, jdbcType=VARCHAR}

mapper.select:
SELECT 
	od.*,
	caitem.xdesc,
	caitem.xgitem,
	caitem.xcatitem 
FROM 
	oporddetail od
	JOIN caitem ON caitem.xitem = od.xitem AND caitem.zid=#{zid}
WHERE 
	od.xordernum=#{xordernum} 
	AND od.xrow=#{xrow} 
	AND od.zid=#{zid} 
	AND od.zactive='1'

SELECT 
	* 
FROM 
	Opordheader 
WHERE 
	xtypetrn=#{xtypetrn}
	AND xpornum=#{xpornum}
	AND xcus=#{xcus}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid} 
	AND zactive='1'
ORDER BY
	XORDERNUM DESC

SELECT 
	* 
FROM 
	Opordheader 
WHERE 
	xtypetrn=#{xtypetrn}
	AND xtrn=#{xtrn}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid} 
	AND zactive='1'
ORDER BY
	XORDERNUM DESC

SELECT 
	OPORDHEADER.*,
	ZBUSINESS.ZORG AS "branchname"
FROM 
	OPORDHEADER
	JOIN ZBUSINESS ON ZBUSINESS.ZID = OPORDHEADER.XCUS
WHERE
	OPORDHEADER.ZID=#{zid}
	AND OPORDHEADER.ZACTIVE='1'
	AND OPORDHEADER.XTYPETRN=#{xtypetrn}
	AND OPORDHEADER.XTRN=#{xtrn}
	AND OPORDHEADER.XSTATUS=#{xstatus}
	AND TO_CHAR(OPORDHEADER.XDATE,'yyyy-MM-dd')=#{xdate}
ORDER BY
	OPORDHEADER.XORDERNUM DESC

SELECT 
	* 
FROM 
	OPORDHEADER 
WHERE 
	(UPPER(XORDERNUM) LIKE '%' || #{xordernum} || '%') 
	AND ZID=#{zid} 
	AND XTYPETRN=#{xtypetrn}
	AND XTRN=#{xtrn}
	AND XSTATUS=#{xstatus}
	AND ZACTIVE='1'
