mapper.delete:
DELETE
FROM
	opreqheader
WHERE
	zid=#{zid}
	AND xdoreqnum=#{xdoreqnum}

DELETE
FROM
	opreqdetail
WHERE
	xdoreqnum=#{xdoreqnum, jdbcType=VARCHAR}
	AND xrow=#{xrow, jdbcType=INTEGER}
	AND zid=#{zid, jdbcType=VARCHAR}

mapper.select:
SELECT 
	* 
FROM 
	opreqdetail 
WHERE 
	xdoreqnum=#{xdoreqnum}
	AND xitem=#{xitem}
	AND zid=#{zid} 
ORDER BY
	XROW DESC
