mapper.delete:
DELETE
FROM 
	pogrnheader
WHERE
	xgrnnum=#{xgrnnum}
	AND zid=#{zid}

mapper.update:
UPDATE
	pogrndetail
SET
	zactive='0',
	zuuserid=#{zuuserid, jdbcType=VARCHAR}
WHERE
	xgrnnum=#{xgrnnum} 
	AND zid=#{zid}

mapper.delete:
DELETE 
FROM 
	pogrndetail 
WHERE 
	xrow=#{xrow} 
	AND xgrnnum=#{xgrnnum} 
	AND zid=#{zid}

mapper.select:
SELECT
	NVL(SUM(pogrndetail.xqtygrn), 0) as totalgrnqty
FROM
	pogrndetail
	LEFT JOIN pogrnheader ON pogrnheader.xgrnnum = pogrndetail.xgrnnum AND pogrnheader.zid = pogrndetail.zid
WHERE
	pogrnheader.zid=#{zid}
	AND pogrnheader.xpornum=#{xpornum}
	AND pogrndetail.xdocrow = #{xdocrow}
	AND pogrnheader.xstatusgrn='Confirmed'
