mapper.select:
SELECT
	*
FROM
	PRO_SUGG
WHERE
	chalan=#{chalan}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid}

SELECT
	*
FROM
	PRO_SUGG
WHERE
	chalan=#{chalan}
	AND xitem=#{xitem}
	AND zid=#{zid}

mapper.delete:
DELETE 
FROM 
	PRO_SUGG 
WHERE 
	chalan=#{chalan}
	AND TO_CHAR(xdate,'yyyy-MM-dd')=#{xdate}
	AND zid=#{zid}
